package com.chipsguide.app.colorbluetoothlamp.v2.brunton.widget;import android.content.Context;import android.graphics.Canvas;import android.graphics.Paint;import android.graphics.Paint.Style;import android.util.AttributeSet;import android.widget.RelativeLayout;import com.chipsguide.app.colorbluetoothlamp.v2.brunton.R;import com.chipsguide.app.colorbluetoothlamp.v2.brunton.utils.MyLogger;public class ColorImageView extends RelativeLayout {	private MyLogger flog = MyLogger.fLog();	private Paint mBitmapPaint = new Paint();;	private float mDrawableRadius = 50f;	private Context mContext;	public ColorImageView(Context context)	{		this(context, null);	}	public ColorImageView(Context context, AttributeSet attrs)	{		this(context, attrs, 0);	}	public ColorImageView(Context context, AttributeSet attrs, int defStyleAttr)	{		super(context, attrs, defStyleAttr);		mContext = context;		init();	}		private void init()	{		mBitmapPaint.setAntiAlias(true);		mBitmapPaint.setStyle(Style.FILL);		mDrawableRadius = mContext.getResources().getDimension(R.dimen.diy_round_size);		flog.d("mDrawableRadius 圆形的半径大小：" + mDrawableRadius);	}		@Override	protected void onDraw(Canvas canvas)	{		super.onDraw(canvas);		canvas.drawCircle(getWidth()/2, getHeight()/2, mDrawableRadius,				mBitmapPaint);	}		public void setImageviewColor(int color)	{		mBitmapPaint.setColor(color);		invalidate();	}}