package com.chipsguide.app.colorbluetoothlamp.v2.utils;import android.graphics.Color;import android.util.Log;public class ColorUtil {	public static int int2Color(int red ,int green ,int blue)	{		if(red < 0)		{			red = 256+red;		}		if(green < 0)		{			green = 256+green;		}		if(blue < 0)		{			blue = 256+blue;		}		return Color.rgb(red, green, blue);	}		public static String color2String(String color)	{		if(color.length() != 2)		{			color = "0" + color;		}		return color;	}		public static String int2color(int[] color)	{		return colorToString(Color.rgb(color[0], color[1], color[2]));	}		public static String colorToString(int color)	{		String alpha = ColorUtil.color2String(Integer.toHexString(Color.alpha(color)));		String red = ColorUtil.color2String(Integer.toHexString(Color.red(color)));		String green = ColorUtil.color2String(Integer.toHexString(Color.green(color)));		String blue = ColorUtil.color2String(Integer.toHexString(Color.blue(color)));		Log.d("fiskz", "alpha:"+alpha+" red : " +  red +" green " + green + " blue " + blue);		return "#" + alpha + red + green + blue;	}	}