package com.chipsguide.app.colorbluetoothlamp.v2.widget;import com.chipsguide.app.colorbluetoothlamp.v2.utils.MyLogger;import android.content.Context;import android.graphics.Canvas;import android.graphics.Paint;import android.graphics.Paint.Style;import android.util.AttributeSet;import android.widget.ImageView;public class ColorImageView extends ImageView {	private MyLogger flog = MyLogger.fLog();	private Paint mBitmapPaint = new Paint();;	private float mDrawableRadius = 50f;	public ColorImageView(Context context)	{		this(context, null);	}	public ColorImageView(Context context, AttributeSet attrs)	{		this(context, attrs, 0);	}	public ColorImageView(Context context, AttributeSet attrs, int defStyleAttr)	{		super(context, attrs, defStyleAttr);		init();	}		private void init()	{		mBitmapPaint.setAntiAlias(true);		mBitmapPaint.setStyle(Style.FILL);	}		@Override	protected void onDraw(Canvas canvas)	{		super.onDraw(canvas);		canvas.drawCircle(getWidth()/2, getHeight()/2, mDrawableRadius,				mBitmapPaint);	}		public void setImageviewColor(int color)	{		flog.e("color " + color);		mBitmapPaint.setColor(color);		invalidate();	}}